(1, '\r\n\r\n\r\n\r\n\r\n	\r\n	\r\n	\r\n		<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01//EN" "http://www.w3.org/TR/html4/strict.dtd">\r\n	\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<html>\r\n\r\n\r\n\r\n\r\n\r\n \r\n\r\n\r\n\r\n\r\n\r\n<!-- Added tag lib in head.jsp by Victor on Jun 11, 07 -->\r\n\r\n<!-- Added tag lib in head.jsp by Victor on Jun 11, 07 -->\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n	<head>\r\n	<META HTTP-EQUIV="Pragma" CONTENT="no-cache">\r\n	<META HTTP-EQUIV="Expires" CONTENT="-1">	\r\n	<META name="Expire" CONTENT="Now">		\r\n	\r\n\r\n<!--  S29048: Embed Pinterest verification  -->\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n	\r\n		<META NAME="robots" content="follow,index">\r\n	\r\n\r\n\r\n	    <META name="p:domain_verify" content="b95ffc40c64bfc38206ae66dd4282ed8"/>\r\n \r\n		<META NAME="abstract" CONTENT="Rogers offers Wireless, Cable TV, Hi-Speed Internet and Home Phone services for home and business.">\r\n		<META NAME="copyright" content="All content presented in this domain may not be used by third parties without the prior written consent of Rogers Communications Inc.">			\r\n		<META HTTP-EQUIV="Content-Language" CONTENT="EN">\r\n		<meta name="PRODCAT" content="wireless">\r\n				\r\n		<meta name="DOCUMENTCOUNTRYCODE" content="ca"> \r\n		<META NAME="author" CONTENT="Rogers Communications Inc.">\r\n		<META NAME="distribution" CONTENT="Global">\r\n		<META NAME="revisit-after" CONTENT="1 days">\r\n		<META NAME="robots" content="follow,index">\r\n		\r\n\r\n<!-- ADDED BY ROGERS.COM SSR RELEASE 1 - END -->		\r\n\r\n	\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<script src="/web/resources/js/common_itsp.js" type="text/javascript"></script>\r\n\r\n <script type="text/javascript">\r\n	var currLineCode = "";\r\n	var inputPostalCode = "";\r\n	var currTargetUrl = "";\r\n	var shoppingLastName = "";\r\n	var shoppingPostalCode = "";\r\n	var shoppingStreetNum="";\r\n	var shoppingAptNum="";\r\n	var shoppingProvince="";\r\n	var shoppingCity="";\r\n	var sessionShoppingProvince="";\r\n	var sessionShoppingCity="";\r\n		\r\n	\r\n	\r\n	if(shoppingProvince==""){\r\n		shoppingProvince = "ON";\r\n	}\r\n	\r\n	\r\n\r\n	\r\n	function showProductInCartBox(){\r\n		showLayerWindow(document.getElementById(''_productInCartBox''));\r\n	}\r\n	\r\n	function showAddressNotEqualBox(){\r\n		showLayerWindow(document.getElementById(''_addressNotEqualMsg''));\r\n	}\r\n			\r\n	function setBoxMessage(tagetUrl, productLine){\r\n			if(productLine=="H"){\r\n				var title = "Rogers Home Phone Service availability";\r\n				var desc = "To ensure that Rogers Home Phone is available in your area, please enter the postal code where you will be using your service.";\r\n				\r\n				if(tagetUrl.indexOf("LD_ORDER")!=-1 ||tagetUrl.indexOf("ldrecotool")!=-1){\r\n					title="Rogers LONG DISTANCE Service availability";\r\n					desc="To ensure that Rogers Long Distance is available in your area, please enter the postal code where you will be using your service.";				\r\n				}\r\n				document.getElementById("h1_RogersServiceavailability").innerHTML=title;\r\n				document.getElementById("p_Toensurethatyougettherightproducts").innerHTML=desc;\r\n			}\r\n			else if(productLine=="I"){\r\n				var title = "Rogers Hi-Speed Internet Service Availability";\r\n				var desc = "To ensure that Rogers Hi-Speed Internet is available in your area, please enter the postal code where you will be using your service.";	\r\n				if(tagetUrl.indexOf("ips")!=-1){\r\n					title = "Rogers Internet Phone Service availability";\r\n					desc = "To ensure that Internet Phone Service is available in your area, please enter the postal code where you will be using your service.";\r\n				}\r\n				else if(tagetUrl.indexOf("portable")!=-1){\r\n					title = "Rogers Portable Internet Service Availability";\r\n					desc = "To ensure that Portable Internet Service is available in your area, please enter the postal code where you will be using your service.";\r\n				}\r\n				else if(tagetUrl.indexOf("dialUp")!=-1){\r\n					title = "Rogers Dial-up Internet Service Availability";\r\n					desc = "To ensure that Dial-up Internet Service is available in your area, please enter the postal code where you will be using your service.";\r\n				}\r\n				document.getElementById("h1_RogersServiceavailability").innerHTML=title;\r\n				document.getElementById("p_Toensurethatyougettherightproducts").innerHTML=desc;\r\n			}	\r\n		}\r\n		//productLine:  H-Home phone  I-Internet W-wireless P-PTV\r\n	function orderProductWithServiceAbilityCheck(tagetUrl, productId, productLine,unPopUp)\r\n	    {\r\n          var pCode = "";\r\n          var apCode="";	\r\n        	currLineCode = productLine;\r\n        	currTargetUrl = tagetUrl;\r\n        	\r\n			document.getElementById(''_targetUrl'').value=tagetUrl;\r\n			document.getElementById(''_productId'').value=productId;\r\n			document.getElementById(''_productLine'').value=productLine;\r\n			document.getElementById(''_postalCode2'').value='''';\r\n\r\n			document.getElementById(''_boxServiceCheck2'').style.display=''none''; \r\n			document.getElementById(''_boxServiceCheck3'').style.display=''none''; \r\n			//serviceCheckForm.productID.value=productID;\r\n		\r\n			if (apCode != "null"&&apCode != ""&&unPopUp=="unPopUp"){ \r\n				\r\n				document.getElementById(''_postalCode2'').value=apCode;  \r\n				document.getElementById(''_frmAvailCheck2'').submit();  \r\n         		return true;\r\n            }\r\n			scroll(0,0);\r\n			document.getElementById(''_boxServiceCheck1'').style.display=''block''; \r\n			document.getElementById(''_postalCodeForm'').style.display=''block''; \r\n			document.getElementById(''_addressForm'').style.display=''none''; \r\n			\r\n			setBoxMessage(tagetUrl, productLine);\r\n			\r\n			//centerDiv(document.getElementById(''_boxServiceCheck1''));\r\n			showLayerWindow(document.getElementById(''_boxServiceCheck1''));\r\n			return false;\r\n		\r\n		}\r\n		\r\n		\r\n	function a_orderProductWithServiceAbilityCheck(tagetUrl, productId, productLine)\r\n	    {\r\n		    document.getElementById("h1_RogersServiceavailability").innerHTML="Rogers Hi-Speed Internet Service Availability";\r\n		    document.getElementById("addProductFromDifferentAddress").innerHTML="You must use the same address information to order this product, as you used to order the other products currently in your cart.";\r\n		    document.getElementById("Whatwouldyouliketodo").innerHTML="Please modify the address information or select one of the options below.";\r\n		    \r\n         	var pCode = "";\r\n        	currLineCode = productLine;\r\n        	currTargetUrl = tagetUrl;\r\n        	\r\n			document.getElementById(''a_targetUrl'').value=tagetUrl;\r\n			document.getElementById(''a_productId'').value=productId;\r\n			document.getElementById(''a_productLine'').value=productLine;\r\n			document.getElementById(''a_postalCode2'').value="";\r\n			document.getElementById(''a_txtStreetName'').value="";\r\n			if (currTargetUrl.indexOf("hispeedCableBuyFlow")!=-1){\r\n				document.getElementById(''a_postalCode2'').readOnly = true;\r\n				//document.getElementById(''a_txtStreetName'').readOnly=true;\r\n			}\r\n			document.getElementById(''a_txtProvince'').value=sessionShoppingProvince;			\r\n			document.getElementById(''a_txtCity'').value=sessionShoppingCity;\r\n				\r\n			\r\n			document.getElementById(''_boxServiceCheck2'').style.display=''none''; \r\n			document.getElementById(''_boxServiceCheck3'').style.display=''none''; \r\n			//serviceCheckForm.productID.value=productID;\r\n			\r\n//			if (pCode != "null"){ \r\n//				document.getElementById(''_frmAvailCheck2'').submit();  \r\n//         		return true;\r\n//          }\r\n			scroll(0,0);\r\n			document.getElementById(''_boxServiceCheck1'').style.display=''block''; \r\n			document.getElementById(''_postalCodeForm'').style.display=''none''; \r\n			document.getElementById(''_addressForm'').style.display=''block''; \r\n			//centerDiv(document.getElementById(''_boxServiceCheck1''));\r\n			showLayerWindow(document.getElementById(''_boxServiceCheck1''));\r\n			return false;\r\n		}\r\n		\r\n	function _boxServiceCheck1Close(){\r\n		//document.getElementById(''_boxServiceCheck1'').style.display=''none''; \r\n		hideLayerWindow(document.getElementById(''_boxServiceCheck1''));\r\n	}\r\n 	function _boxNoServiceClose(){\r\n 		document.getElementById(''_boxNoService'').style.display=''none'';\r\n 	}\r\n\r\n	function orderHomePhoneProduct(tagetUrl, productId){\r\n		orderProductWithServiceAbilityCheck(tagetUrl, productId, "H");\r\n	}\r\n	\r\n	function orderInternetProduct(tagetUrl, productId){\r\n		orderProductWithServiceAbilityCheck(tagetUrl, productId, "I");\r\n	}	\r\n	\r\n	function orderInternetProductUnPop(tagetUrl, productId){\r\n		orderProductWithServiceAbilityCheck(tagetUrl, productId, "I", "unPopUp");\r\n	}\r\n	\r\n	function orderHomePhoneProductUnPop(tagetUrl, productId){\r\n		orderProductWithServiceAbilityCheck(tagetUrl, productId, "H", "unPopUp");\r\n	}\r\n	\r\n	function orderHispeedProduct(tagetUrl, productId){\r\n		a_orderProductWithServiceAbilityCheck(tagetUrl, productId, "I");\r\n	}\r\n	function orderPTVProduct(tagetUrl, productId){\r\n		a_orderProductWithServiceAbilityCheck(tagetUrl, productId, "P");\r\n	}		\r\n	function getOrderPromotionURL(promotionId, promotionType, productId){\r\n		var targetUrl = "null";\r\n		if(targetUrl=="" || targetUrl=="null"){\r\n			return "";\r\n		}\r\n		\r\n		if(targetUrl.indexOf("?")==-1)\r\n			targetUrl+="?";\r\n		targetUrl+="&promotionId="+promotionId+"&promotionType="+promotionType+"&productCode="+productId;\r\n		return targetUrl;\r\n	}\r\n	\r\n	function orderHPPromotion(promotionId, promotionType, productId){\r\n		var targetUrl = "/web/link/hpOrderPromotion?";//getOrderPromotionURL(promotionId, promotionType, productId);\r\n		targetUrl+="&promotionId="+promotionId+"&promotionType="+promotionType+"&productCode="+productId;\r\n		orderHomePhoneProduct(targetUrl, productId);\r\n	}\r\n\r\n	function orderHPPromotionUnPop(promotionId, promotionType, productId){\r\n		var targetUrl = "/web/link/hpOrderPromotion?";//getOrderPromotionURL(promotionId, promotionType, productId);\r\n		targetUrl+="&promotionId="+promotionId+"&promotionType="+promotionType+"&productCode="+productId;\r\n		orderHomePhoneProductUnPop(targetUrl, productId);\r\n	}	\r\n	\r\n	function orderHPPromotionUnPop(promotionId, promotionType, productId, contractCode){\r\n		var targetUrl = "/web/link/hpOrderPromotion?";//getOrderPromotionURL(promotionId, promotionType, productId);		\r\n		targetUrl+="&promotionId="+promotionId+"&promotionType="+promotionType+"&productCode="+productId+"&contractCode="+contractCode;\r\n		orderHomePhoneProductUnPop(targetUrl, productId);\r\n	}	\r\n	\r\n		\r\n	function orderLDPromotion(promotionId, promotionType, productId){\r\n		var targetUrl = "/web/link/ldOrderPromotion?";\r\n		targetUrl+="&promotionId="+promotionId+"&promotionType="+promotionType+"&productCode="+productId;\r\n		orderHomePhoneProduct(targetUrl, productId);\r\n	}\r\n	\r\n	function orderIPSPromotion(promotionId, promotionType, productId){\r\n		var targetUrl = "/web/link/ipsOrderPromotion?";\r\n		targetUrl+="&promotionId="+promotionId+"&promotionType="+promotionType+"&productCode="+productId;\r\n		orderInternetProduct(targetUrl, productId);\r\n	}	\r\n	\r\n	function openHiSpeedPromotionPage(serviceCodes, promotionUrls){\r\n		var targetUrl = "/web/link/openPromotionPage?";\r\n		targetUrl+="&_serviceCode="+serviceCodes+"&_promotionUrl="+encodeURIComponent(promotionUrls)+"&_productLine=I";\r\n		orderInternetProduct(targetUrl,"");\r\n	}\r\n	\r\n	function openRecomendationToolPage(serviceCodes, promotionUrls){\r\n		var targetUrl = "/web/link/openPromotionPage?";\r\n		targetUrl+="&_serviceCode="+serviceCodes+"&_promotionUrl="+encodeURIComponent(promotionUrls)+"&_productLine=I";				\r\n		orderInternetProduct(targetUrl,"");\r\n	}\r\n		\r\n	function _submitValid()\r\n	{\r\n		var empty = true;\r\n		var provinceCode = "KLMNP";\r\n		var sessionProvince="ON"\r\n		\r\n		_errorMsgClose();\r\n		\r\n		var obj = null;\r\n		if (document.getElementById(''_postalCodeForm'').style.display==''block''){\r\n			obj = document.getElementById("_postalCode2");\r\n		}else{\r\n			obj = document.getElementById("a_postalCode2");\r\n		}\r\n		\r\n		var pc = obj.value;\r\n		\r\n		pc=trimSpaces(pc).toUpperCase();\r\n		\r\n		inputPostalCode = pc;\r\n\r\n		if(pc!="" && postcodevalidate("ca",pc))	{\r\n			var sameSessionProvince = provinceEqual(sessionProvince,pc);\r\n			var sameShoppingProvince = provinceEqual(shoppingProvince,pc);\r\n  			if(sameSessionProvince && sameShoppingProvince){ //added wireless province\r\n  				return true;\r\n 			}else{\r\n				\r\n				if(!sameShoppingProvince && !empty){\r\n					_boxServiceCheck1Close();\r\n	 				showAddressNotEqualBox();\r\n					return false;\r\n				}\r\n				if(!sameSessionProvince && empty){\r\n	 				document.getElementById(''_boxServiceCheck4'').style.display=''block''; \r\n					return false;\r\n				}\r\n				\r\n 				return true;\r\n 			}	 					\r\n	 	}\r\n	 	else{	 	\r\n		 	if (pc==""){	 	\r\n			 	document.getElementById(''_boxServiceCheck3'').style.display=''block''; \r\n			 	\r\n		  	}else {\r\n		  		document.getElementById(''_boxServiceCheck2'').style.display=''block''; \r\n		  	}\r\n		 	\r\n		 	obj.value='''';\r\n		 	obj.focus();\r\n		  	return false;\r\n	 	}\r\n	\r\n	}	\r\n	\r\n\r\n	function _errorMsgClose(){\r\n		document.getElementById(''_boxServiceCheck2'').style.display=''none'';\r\n		document.getElementById(''_boxServiceCheck3'').style.display=''none'';\r\n		document.getElementById(''_boxServiceCheck4'').style.display=''none'';\r\n	}\r\n	\r\n	function _cableAddressCheck(){\r\n		var retu = false;\r\n		if(_submitValid()){\r\n\r\n			var checkPackage=false;\r\n			var inBrowseFlow=false;\r\n\r\n			if(currLineCode=="H" && (currTargetUrl.indexOf("hpBrowseFlow")!=-1 ))\r\n				inBrowseFlow=true;\r\n\r\n			if(currLineCode=="H" && (currTargetUrl.indexOf("HPH_ORDER")!=-1 || currTargetUrl.indexOf("hpOrderPromotion")!=-1) && "false" == "true")//check HP package\r\n				checkPackage=true;\r\n\r\n			if(currLineCode=="H" && currTargetUrl.indexOf("hprecotool")!=-1 && "false" == "true")//check HP reco tool \r\n				checkPackage=true;\r\n\r\n			if(currLineCode=="I" && (currTargetUrl.indexOf("ipsBuyFlowBegin")!=-1 || currTargetUrl.indexOf("ipsOrderPromotion")!=-1 || currTargetUrl.indexOf("ipsCheckServiceabilityByPostalCode")!=-1) && "false" == "true")//check IPS package\r\n				checkPackage=true;\r\n			\r\n			if(currLineCode=="I" && currTargetUrl.indexOf("dialUpServiceBegin")!=-1 && "false" == "true")//check IPS package\r\n				checkPackage=true;	\r\n			if(currLineCode=="I" && currTargetUrl.indexOf("portableBuyFlowBegin")!=-1 && "false" == "true")//check portable package\r\n				checkPackage=true;	\r\n			if(currLineCode=="I" && currTargetUrl.indexOf("hispeedDSLBuyFlow")!=-1 && "false" == "true")//check DSL package\r\n				checkPackage=true;	\r\n			 \r\n			 \r\n			 var linkCableBuyflow=false;\r\n			 var hispeedCableAddressEqual = false;\r\n			 if(currLineCode=="I" && currTargetUrl.indexOf("hispeedCableBuyFlow")!=-1){\r\n			 	 linkCableBuyflow=true;\r\n			  	\r\n			  	var inputLastName=getCompareString(document.getElementById("a_txtLastName").value);\r\n			  	var inputStreetNum=getCompareString(document.getElementById("a_txtStreetNum").value);\r\n			  	var inputAptNum=getCompareString(document.getElementById("a_txtAptNum").value);\r\n			 	hispeedCableAddressEqual=(shoppingLastName==''''||getCompareString(shoppingLastName)==getCompareString(inputLastName))\r\n			 			&&(shoppingStreetNum==''''||shoppingStreetNum==inputStreetNum)\r\n			 			&&(shoppingAptNum==''''||shoppingAptNum== inputAptNum)\r\n			 			&&(getCompareString(inputPostalCode)==getCompareString(shoppingPostalCode)\r\n			 		);	\r\n			 	\r\n			  }\r\n			  \r\n			_boxServiceCheck1Close();  \r\n\r\n			if(shoppingPostalCode=="null" || shoppingPostalCode=="")\r\n				retu = true;\r\n			els